import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';

const Sidebar = () => {
  const location = useLocation();

  const navItems = [
    { path: '/dashboard', label: 'Dashboard', icon: 'ðŸ“Š' },
    { path: '/bench-profiles', label: 'Bench Profiles', icon: 'ðŸ‘¥' },
    { path: '/working-candidates', label: 'Working Candidates', icon: 'ðŸ’¼' },
    { path: '/candidates', label: 'All Candidates', icon: 'ðŸ“‹' },
    { path: '/candidates/new', label: 'Add Candidate', icon: 'âž•' },
  ];

  return (
    <aside className="sidebar">
      <div className="sidebar-header">
        <h2 className="sidebar-title">CRM System</h2>
      </div>
      
      <nav className="sidebar-nav">
        {navItems.map((item) => (
          <NavLink
            key={item.path}
            to={item.path}
            className={({ isActive }) => 
              `nav-item ${isActive || location.pathname === item.path ? 'active' : ''}`
            }
          >
            <span style={{ marginRight: '0.75rem' }}>{item.icon}</span>
            {item.label}
          </NavLink>
        ))}
      </nav>
    </aside>
  );
};

export default Sidebar;
