sourcesContent:
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { benchCandidatesAPI, candidateActivitiesAPI } from '../../services/api';
import toast from 'react-hot-toast';

const BenchCandidateDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [candidate, setCandidate] = useState(null);
  const [activities, setActivities] = useState([]);
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showActivityForm, setShowActivityForm] = useState(false);
  const [activeTab, setActiveTab] = useState('documents');
  const [documentPreview, setDocumentPreview] = useState(null);
  const [newDocuments, setNewDocuments] = useState([]);

  const [activityForm, setActivityForm] = useState({
    activityType: 'APPLIED',
    clientName: '',
    contactPerson: '',
    contactPhone: '',
    contactEmail: '',
    submittedRate: '',
    notes: '',
    activityDate: new Date().toISOString().split('T')[0]
  });

  useEffect(() => {
    fetchCandidateDetails();
    fetchActivities();
    fetchDocuments();
  }, [id]);

  const fetchCandidateDetails = async () => {
    try {
      const response = await benchCandidatesAPI.getById(id);
      setCandidate(response.data);
    } catch (error) {
      toast.error('Failed to load candidate details');
      navigate('/bench-candidates');
    } finally {
      setLoading(false);
    }
  };

  const fetchActivities = async () => {
    try {
      const response = await candidateActivitiesAPI.getByCandidateId(id);
      setActivities(response.data);
    } catch (error) {
      console.error('Failed to load activities:', error);
    }
  };

  const fetchDocuments = async () => {
    try {
      // This will fetch documents associated with the candidate
      const response = await benchCandidatesAPI.getDocuments(id);
      setDocuments(response.data || []);
    } catch (error) {
      console.error('Failed to load documents:', error);
      // For now, create mock documents based on uploaded files
      if (candidate?.resumeFilename) {
        setDocuments([
          {
            id: 1,
            filename: candidate.resumeFilename,
            uploadDate: candidate.createdAt,
            size: '2.5 MB',
            type: 'pdf'
          }
        ]);
      }
    }
  };

  const handleActivityFormChange = (e) => {
    setActivityForm({
      ...activityForm,
      [e.target.name]: e.target.value,
    });
  };

  const handleAddActivity = async (e) => {
    e.preventDefault();
    try {
      await candidateActivitiesAPI.create({
        ...activityForm,
        candidateId: id
      });
      toast.success('Activity added successfully!');
      setShowActivityForm(false);
      setActivityForm({
        activityType: 'APPLIED',
        clientName: '',
        contactPerson: '',
        contactPhone: '',
        contactEmail: '',
        submittedRate: '',
        notes: '',
        activityDate: new Date().toISOString().split('T')[0]
      });
      fetchActivities();
    } catch (error) {
      toast.error('Failed to add activity');
    }
  };

  const handleDocumentUpload = async (e) => {
    const files = Array.from(e.target.files);
    if (files.length === 0) return;

    try {
      const formData = new FormData();
      files.forEach(file => {
        formData.append('documents', file);
      });
      formData.append('candidateId', id);

      await benchCandidatesAPI.uploadDocuments(id, formData);
      toast.success('Documents uploaded successfully!');
      fetchDocuments();
      setNewDocuments([]);
    } catch (error) {
      toast.error('Failed to upload documents');
    }
  };

  const handleDownloadDocument = async (docId, filename) => {
    try {
      const response = await benchCandidatesAPI.downloadDocument(id, docId);
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', filename);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      toast.error('Failed to download document');
    }
  };

  const handlePreviewDocument = async (docId, filename) => {
    try {
      const response = await benchCandidatesAPI.downloadDocument(id, docId);
      const blob = new Blob([response.data]);
      const url = window.URL.createObjectURL(blob);

      setDocumentPreview({
        url,
        filename,
        type: filename.split('.').pop().toLowerCase()
      });
    } catch (error) {
      toast.error('Failed to preview document');
    }
  };

  const handleDeleteDocument = async (docId) => {
    if (window.confirm('Are you sure you want to delete this document?')) {
      try {
        await benchCandidatesAPI.deleteDocument(id, docId);
        toast.success('Document deleted successfully!');
        fetchDocuments();
      } catch (error) {
        toast.error('Failed to delete document');
      }
    }
  };

  const getActivityTypeColor = (type) => {
    const colors = {
      'APPLIED': '#3B82F6',
      'SUBMITTED': '#10B981',
      'INTERVIEW_SCHEDULED': '#F59E0B',
      'INTERVIEW_COMPLETED': '#8B5CF6',
      'FEEDBACK_RECEIVED': '#6B7280',
      'REJECTED': '#EF4444',
      'ON_HOLD': '#F97316'
    };
    return colors[type] || '#6B7280';
  };

  const getActivityTypeDisplay = (type) => {
    const displays = {
      'APPLIED': 'Applied',
      'SUBMITTED': 'Submitted',
      'INTERVIEW_SCHEDULED': 'Interview Scheduled',
      'INTERVIEW_COMPLETED': 'Interview Completed',
      'FEEDBACK_RECEIVED': 'Feedback Received',
      'REJECTED': 'Rejected',
      'ON_HOLD': 'On Hold'
    };
    return displays[type] || type;
  };

  const getFileIcon = (filename) => {
    const ext = filename.split('.').pop().toLowerCase();
    switch (ext) {
      case 'pdf': return 'üìÑ';
      case 'doc':
      case 'docx': return 'üìù';
      case 'jpg':
      case 'jpeg':
      case 'png': return 'üñºÔ∏è';
      default: return 'üìé';
    }
  };

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '400px' }}>
        <div>Loading candidate details...</div>
      </div>
    );
  }

  if (!candidate) {
    return (
      <div style={{ textAlign: 'center', padding: '2rem' }}>
        <h2>Candidate not found</h2>
        <Link to="/bench-candidates" className="btn-primary">Back to Bench Profiles</Link>
      </div>
    );
  }

  return (
    <div>
      {/* Header */}
      <div style={{ marginBottom: '2rem', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div>
          <h1 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#1F2937', marginBottom: '0.5rem' }}>
            {candidate.fullName}
          </h1>
          <p style={{ color: '#6B7280' }}>
            Bench Profile Details & Management
          </p>
        </div>
        <div style={{ display: 'flex', gap: '1rem' }}>
          <Link to={`/bench-candidates/edit/${id}`} className="btn-secondary">
            ‚úèÔ∏è Edit Profile
          </Link>
          <Link to="/bench-candidates" className="btn-secondary">
            ‚Üê Back to List
          </Link>
        </div>
      </div>

      {/* Candidate Details Card */}
      <div style={{ 
        background: 'white', 
        borderRadius: '12px', 
        boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
        padding: '2rem',
        marginBottom: '2rem'
      }}>
        <h3 style={{ fontSize: '1.25rem', fontWeight: '600', marginBottom: '1.5rem' }}>
          üë§ Profile Information
        </h3>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1rem' }}>
          <div><strong>Visa Status:</strong> {candidate.visaStatus}</div>
          <div><strong>Location:</strong> {candidate.city}, {candidate.state}</div>
          <div><strong>Primary Skill:</strong> {candidate.primarySkill}</div>
          <div><strong>Experience:</strong> {candidate.experienceYears} years</div>
          <div><strong>Phone:</strong> {candidate.phoneNumber}</div>
          <div><strong>Email:</strong> {candidate.email}</div>
          {candidate.targetRate && <div><strong>Target Rate:</strong> ${candidate.targetRate}/hr</div>}
          {candidate.assignedConsultantName && <div><strong>Assigned Consultant:</strong> {candidate.assignedConsultantName}</div>}
        </div>

        {candidate.notes && (
          <div style={{ marginTop: '1rem', padding: '1rem', backgroundColor: '#F3F4F6', borderRadius: '8px' }}>
            <strong>Notes:</strong> {candidate.notes}
          </div>
        )}
      </div>

      {/* Tab Navigation */}
      <div style={{ 
        background: 'white', 
        borderRadius: '12px', 
        boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
        overflow: 'hidden'
      }}>
        <div style={{ 
          display: 'flex',
          borderBottom: '2px solid #E5E7EB'
        }}>
          <button
            onClick={() => setActiveTab('documents')}
            style={{
              padding: '1rem 2rem',
              background: activeTab === 'documents' ? '#3B82F6' : 'transparent',
              color: activeTab === 'documents' ? 'white' : '#6B7280',
              border: 'none',
              fontSize: '1rem',
              fontWeight: '600',
              cursor: 'pointer',
              transition: 'all 0.3s ease'
            }}
          >
            üìÑ Documents
          </button>
          <button
            onClick={() => setActiveTab('activity')}
            style={{
              padding: '1rem 2rem',
              background: activeTab === 'activity' ? '#3B82F6' : 'transparent',
              color: activeTab === 'activity' ? 'white' : '#6B7280',
              border: 'none',
              fontSize: '1rem',
              fontWeight: '600',
              cursor: 'pointer',
              transition: 'all 0.3s ease'
            }}
          >
            üìä Daily Activity
          </button>
          <button
            onClick={() => setActiveTab('progress')}
            style={{
              padding: '1rem 2rem',
              background: activeTab === 'progress' ? '#3B82F6' : 'transparent',
              color: activeTab === 'progress' ? 'white' : '#6B7280',
              border: 'none',
              fontSize: '1rem',
              fontWeight: '600',
              cursor: 'pointer',
              transition: 'all 0.3s ease'
            }}
          >
            üìà Progress
          </button>
        </div>

        {/* Tab Content */}
        <div style={{ padding: '2rem' }}>
          {/* Documents Tab */}
          {activeTab === 'documents' && (
            <div>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
                <h3 style={{ fontSize: '1.25rem', fontWeight: '600' }}>
                  Document Library
                </h3>
                <div>
                  <input
                    type="file"
                    id="uploadDocs"
                    multiple
                    accept=".pdf,.doc,.docx,.jpg,.jpeg,.png"
                    onChange={handleDocumentUpload}
                    style={{ display: 'none' }}
                  />
                  <label
                    htmlFor="uploadDocs"
                    className="btn-primary"
                    style={{ cursor: 'pointer', display: 'inline-block' }}
                  >
                    üìé Upload Documents
                  </label>
                </div>
              </div>

              {documents.length > 0 ? (
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '1rem' }}>
                  {documents.map((doc, index) => (
                    <div 
                      key={doc.id || index}
                      style={{ 
                        border: '1px solid #E5E7EB',
                        borderRadius: '8px',
                        padding: '1.5rem',
                        backgroundColor: '#FAFAFA',
                        transition: 'all 0.3s ease'
                      }}
                      onMouseEnter={(e) => e.target.style.backgroundColor = '#F3F4F6'}
                      onMouseLeave={(e) => e.target.style.backgroundColor = '#FAFAFA'}
                    >
                      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '1rem' }}>
                        <span style={{ fontSize: '2rem', marginRight: '0.75rem' }}>
                          {getFileIcon(doc.filename || doc.resumeFilename || 'file')}
                        </span>
                        <div style={{ flex: 1 }}>
                          <h4 style={{ fontSize: '1rem', fontWeight: '600', marginBottom: '0.25rem' }}>
                            {doc.filename || doc.resumeFilename || 'Document'}
                          </h4>
                          <p style={{ fontSize: '0.875rem', color: '#6B7280' }}>
                            {doc.size || '2.5 MB'} ‚Ä¢ Uploaded {new Date(doc.uploadDate || doc.createdAt || Date.now()).toLocaleDateString()}
                          </p>
                        </div>
                      </div>

                      <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
                        <button
                          onClick={() => handlePreviewDocument(doc.id || 1, doc.filename || doc.resumeFilename)}
                          style={{
                            background: '#3B82F6',
                            color: 'white',
                            border: 'none',
                            padding: '0.5rem 1rem',
                            borderRadius: '6px',
                            fontSize: '0.875rem',
                            cursor: 'pointer',
                            display: 'flex',
                            alignItems: 'center',
                            gap: '0.25rem'
                          }}
                        >
                          üëÅÔ∏è Preview
                        </button>
                        <button
                          onClick={() => handleDownloadDocument(doc.id || 1, doc.filename || doc.resumeFilename)}
                          style={{
                            background: '#10B981',
                            color: 'white',
                            border: 'none',
                            padding: '0.5rem 1rem',
                            borderRadius: '6px',
                            fontSize: '0.875rem',
                            cursor: 'pointer',
                            display: 'flex',
                            alignItems: 'center',
                            gap: '0.25rem'
                          }}
                        >
                          ‚¨áÔ∏è Download
                        </button>
                        <button
                          onClick={() => handleDeleteDocument(doc.id || 1)}
                          style={{
                            background: '#EF4444',
                            color: 'white',
                            border: 'none',
                            padding: '0.5rem 1rem',
                            borderRadius: '6px',
                            fontSize: '0.875rem',
                            cursor: 'pointer',
                            display: 'flex',
                            alignItems: 'center',
                            gap: '0.25rem'
                          }}
                        >
                          üóëÔ∏è Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div style={{ textAlign: 'center', padding: '3rem', color: '#6B7280' }}>
                  <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üìÑ</div>
                  <h3 style={{ fontSize: '1.25rem', marginBottom: '0.5rem' }}>No documents uploaded yet</h3>
                  <p style={{ fontSize: '0.875rem', marginBottom: '1.5rem' }}>
                    Upload resumes, certificates, and other important documents
                  </p>
                  <label
                    htmlFor="uploadDocs"
                    className="btn-primary"
                    style={{ cursor: 'pointer', display: 'inline-block' }}
                  >
                    üìé Upload First Document
                  </label>
                </div>
              )}
            </div>
          )}

          {/* Daily Activity Tab */}
          {activeTab === 'activity' && (
            <div>
              <div style={{ 
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                marginBottom: '1.5rem'
              }}>
                <h3 style={{ fontSize: '1.25rem', fontWeight: '600' }}>
                  üìä Daily Activities & Progress
                </h3>
                <button 
                  onClick={() => setShowActivityForm(!showActivityForm)}
                  className="btn-primary"
                >
                  {showActivityForm ? 'Cancel' : '+ Add Today\'s Activity'}
                </button>
              </div>

              {/* Activity Form */}
              {showActivityForm && (
                <div style={{ padding: '2rem', borderBottom: '1px solid #E5E7EB', backgroundColor: '#F9FAFB', borderRadius: '8px', marginBottom: '2rem' }}>
                  <h4 style={{ marginBottom: '1rem', fontSize: '1.125rem', fontWeight: '600' }}>
                    Record Today's Activity for {candidate.fullName}
                  </h4>
                  <form onSubmit={handleAddActivity}>
                    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1rem', marginBottom: '1rem' }}>
                      <div className="form-group">
                        <label className="form-label">Activity Type *</label>
                        <select
                          name="activityType"
                          className="form-input"
                          value={activityForm.activityType}
                          onChange={handleActivityFormChange}
                          required
                        >
                          <option value="APPLIED">Applied to Job</option>
                          <option value="SUBMITTED">Submitted to Client</option>
                          <option value="INTERVIEW_SCHEDULED">Interview Scheduled</option>
                          <option value="INTERVIEW_COMPLETED">Interview Completed</option>
                          <option value="FEEDBACK_RECEIVED">Feedback Received</option>
                          <option value="REJECTED">Rejected</option>
                          <option value="ON_HOLD">On Hold</option>
                        </select>
                      </div>

                      <div className="form-group">
                        <label className="form-label">Client/Company Name *</label>
                        <input
                          type="text"
                          name="clientName"
                          className="form-input"
                          value={activityForm.clientName}
                          onChange={handleActivityFormChange}
                          required
                          placeholder="e.g. Google, Microsoft, Amazon"
                        />
                      </div>

                      <div className="form-group">
                        <label className="form-label">Point of Contact</label>
                        <input
                          type="text"
                          name="contactPerson"
                          className="form-input"
                          value={activityForm.contactPerson}
                          onChange={handleActivityFormChange}
                          placeholder="Contact person name"
                        />
                      </div>

                      <div className="form-group">
                        <label className="form-label">Contact Phone</label>
                        <input
                          type="tel"
                          name="contactPhone"
                          className="form-input"
                          value={activityForm.contactPhone}
                          onChange={handleActivityFormChange}
                          placeholder="(555) 123-4567"
                        />
                      </div>

                      <div className="form-group">
                        <label className="form-label">Contact Email</label>
                        <input
                          type="email"
                          name="contactEmail"
                          className="form-input"
                          value={activityForm.contactEmail}
                          onChange={handleActivityFormChange}
                          placeholder="contact@company.com"
                        />
                      </div>

                      <div className="form-group">
                        <label className="form-label">Submitted Rate ($/hr)</label>
                        <input
                          type="number"
                          name="submittedRate"
                          className="form-input"
                          value={activityForm.submittedRate}
                          onChange={handleActivityFormChange}
                          min="0"
                          step="0.01"
                          placeholder="e.g. 85"
                        />
                      </div>

                      <div className="form-group">
                        <label className="form-label">Activity Date *</label>
                        <input
                          type="date"
                          name="activityDate"
                          className="form-input"
                          value={activityForm.activityDate}
                          onChange={handleActivityFormChange}
                          required
                        />
                      </div>
                    </div>

                    <div className="form-group">
                      <label className="form-label">Notes</label>
                      <textarea
                        name="notes"
                        className="form-input"
                        value={activityForm.notes}
                        onChange={handleActivityFormChange}
                        rows="3"
                        placeholder="Additional notes about this activity..."
                      />
                    </div>

                    <div style={{ display: 'flex', gap: '1rem' }}>
                      <button type="submit" className="btn-primary">
                        Add Activity
                      </button>
                      <button 
                        type="button" 
                        onClick={() => setShowActivityForm(false)}
                        className="btn-secondary"
                      >
                        Cancel
                      </button>
                    </div>
                  </form>
                </div>
              )}

              {/* Activities List */}
              {activities.length > 0 ? (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
                  {activities.map((activity) => (
                    <div 
                      key={activity.id}
                      style={{ 
                        border: '1px solid #E5E7EB',
                        borderRadius: '8px',
                        padding: '1.5rem',
                        backgroundColor: '#FAFAFA'
                      }}
                    >
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '0.75rem' }}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                          <span 
                            style={{
                              background: getActivityTypeColor(activity.activityType),
                              color: 'white',
                              padding: '0.5rem 1rem',
                              borderRadius: '9999px',
                              fontSize: '0.875rem',
                              fontWeight: '600'
                            }}
                          >
                            {getActivityTypeDisplay(activity.activityType)}
                          </span>
                          <strong style={{ fontSize: '1.1rem' }}>{activity.clientName}</strong>
                        </div>
                        <span style={{ color: '#6B7280', fontSize: '0.875rem', fontWeight: '500' }}>
                          {new Date(activity.activityDate).toLocaleDateString()}
                        </span>
                      </div>

                      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '0.75rem', fontSize: '0.875rem', marginBottom: '1rem' }}>
                        {activity.contactPerson && <div><strong>Contact:</strong> {activity.contactPerson}</div>}
                        {activity.contactPhone && <div><strong>Phone:</strong> {activity.contactPhone}</div>}
                        {activity.contactEmail && <div><strong>Email:</strong> {activity.contactEmail}</div>}
                        {activity.submittedRate && <div><strong>Rate:</strong> <span style={{ color: '#10B981', fontWeight: '600' }}>${activity.submittedRate}/hr</span></div>}
                      </div>

                      {activity.notes && (
                        <div style={{ marginTop: '0.75rem', fontSize: '0.875rem', color: '#6B7280', fontStyle: 'italic', padding: '0.5rem', backgroundColor: '#F3F4F6', borderRadius: '4px' }}>
                          <strong>Notes:</strong> {activity.notes}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              ) : (
                <div style={{ textAlign: 'center', color: '#6B7280', padding: '3rem' }}>
                  <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üìä</div>
                  <h3 style={{ fontSize: '1.25rem', marginBottom: '0.5rem' }}>No activities recorded yet</h3>
                  <p style={{ fontSize: '0.875rem', marginBottom: '1.5rem' }}>
                    Start tracking daily progress by clicking "Add Today's Activity" above
                  </p>
                  <button 
                    onClick={() => setShowActivityForm(true)}
                    className="btn-primary"
                  >
                    Add First Activity
                  </button>
                </div>
              )}
            </div>
          )}

          {/* Progress Tab */}
          {activeTab === 'progress' && (
            <div style={{ textAlign: 'center', padding: '3rem', color: '#6B7280' }}>
              <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üìà</div>
              <h3 style={{ fontSize: '1.25rem', marginBottom: '0.5rem' }}>Progress Tracking</h3>
              <p style={{ fontSize: '0.875rem' }}>
                Advanced progress tracking features coming soon...
              </p>
            </div>
          )}
        </div>
      </div>

      {/* Document Preview Modal */}
      {documentPreview && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.8)',
          zIndex: 1000,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          padding: '2rem'
        }}>
          <div style={{
            backgroundColor: 'white',
            borderRadius: '12px',
            maxWidth: '90vw',
            maxHeight: '90vh',
            overflow: 'auto',
            position: 'relative'
          }}>
            <div style={{
              padding: '1rem 2rem',
              borderBottom: '1px solid #E5E7EB',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center'
            }}>
              <h3>{documentPreview.filename}</h3>
              <button
                onClick={() => setDocumentPreview(null)}
                style={{
                  background: '#EF4444',
                  color: 'white',
                  border: 'none',
                  padding: '0.5rem 1rem',
                  borderRadius: '6px',
                  cursor: 'pointer'
                }}
              >
                ‚úï Close
              </button>
            </div>
            <div style={{ padding: '2rem', textAlign: 'center' }}>
              {documentPreview.type === 'pdf' ? (
                <iframe
                  src={documentPreview.url}
                  style={{ width: '70vw', height: '70vh', border: 'none' }}
                  title="Document Preview"
                />
              ) : ['jpg', 'jpeg', 'png'].includes(documentPreview.type) ? (
                <img
                  src={documentPreview.url}
                  alt="Document Preview"
                  style={{ maxWidth: '100%', maxHeight: '70vh' }}
                />
              ) : (
                <div>
                  <p>Preview not available for this file type.</p>
                  <button
                    onClick={() => handleDownloadDocument(1, documentPreview.filename)}
                    className="btn-primary"
                  >
                    Download File
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BenchCandidateDetail;
