import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Box,
  Alert,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Chip
} from '@mui/material';
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  Save as SaveIcon,
  Cancel as CancelIcon
} from '@mui/icons-material';

const VendorForm = () => {
  // State for form data - matching your entity structure
  const [formData, setFormData] = useState({
    companyName: '',
    primaryContactName: '',
    primaryContactEmail: '',
    primaryContactPhone: '',
    address: '',
    city: '',
    state: '',
    zipCode: '',
    country: '',
    preferredSkills: '',
    rateRangeMin: '',
    rateRangeMax: '',
    totalSubmissions: 0,
    successfulPlacements: 0,
    notes: '',
    status: 'ACTIVE'
  });

  // State for component management
  const [vendors, setVendors] = useState([]);
  const [loading, setLoading] = useState(false);
  const [submitLoading, setSubmitLoading] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [errors, setErrors] = useState({});
  const [openDialog, setOpenDialog] = useState(false);
  const [deleteId, setDeleteId] = useState(null);

  // Constants
  const STATUS_OPTIONS = [
    'ACTIVE',
    'INACTIVE',
    'PREFERRED',
    'SUSPENDED'
  ];

  // Load vendors on component mount
  useEffect(() => {
    fetchVendors();
  }, []);

  // Fetch all vendors
  const fetchVendors = async () => {
    setLoading(true);
    try {
      const response = await axios.get('/api/vendors');
      setVendors(response.data);
    } catch (error) {
      console.error('Error fetching vendors:', error);
      toast.error('Failed to load vendors');
    } finally {
      setLoading(false);
    }
  };

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error for this field
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Validate form
  const validateForm = () => {
    const newErrors = {};

    if (!formData.companyName.trim()) {
      newErrors.companyName = 'Company name is required';
    }

    if (!formData.primaryContactName.trim()) {
      newErrors.primaryContactName = 'Primary contact name is required';
    }

    if (!formData.primaryContactEmail.trim()) {
      newErrors.primaryContactEmail = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.primaryContactEmail)) {
      newErrors.primaryContactEmail = 'Email format is invalid';
    }

    if (formData.rateRangeMin && isNaN(formData.rateRangeMin)) {
      newErrors.rateRangeMin = 'Minimum rate must be a number';
    }

    if (formData.rateRangeMax && isNaN(formData.rateRangeMax)) {
      newErrors.rateRangeMax = 'Maximum rate must be a number';
    }

    if (formData.rateRangeMin && formData.rateRangeMax && 
        parseFloat(formData.rateRangeMin) > parseFloat(formData.rateRangeMax)) {
      newErrors.rateRangeMax = 'Maximum rate must be greater than minimum rate';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      toast.error('Please fix the form errors');
      return;
    }

    setSubmitLoading(true);
    try {
      const payload = {
        ...formData,
        rateRangeMin: formData.rateRangeMin ? parseFloat(formData.rateRangeMin) : null,
        rateRangeMax: formData.rateRangeMax ? parseFloat(formData.rateRangeMax) : null,
        totalSubmissions: parseInt(formData.totalSubmissions) || 0,
        successfulPlacements: parseInt(formData.successfulPlacements) || 0
      };

      if (editMode) {
        await axios.put(`/api/vendors/${editingId}`, payload);
        toast.success('Vendor updated successfully!');
      } else {
        await axios.post('/api/vendors', payload);
        toast.success('Vendor created successfully!');
      }

      resetForm();
      fetchVendors();
    } catch (error) {
      console.error('Error saving vendor:', error);
      if (error.response?.data?.message) {
        toast.error(error.response.data.message);
      } else {
        toast.error(editMode ? 'Failed to update vendor' : 'Failed to create vendor');
      }
    } finally {
      setSubmitLoading(false);
    }
  };

  // Reset form
  const resetForm = () => {
    setFormData({
      companyName: '',
      primaryContactName: '',
      primaryContactEmail: '',
      primaryContactPhone: '',
      address: '',
      city: '',
      state: '',
      zipCode: '',
      country: '',
      preferredSkills: '',
      rateRangeMin: '',
      rateRangeMax: '',
      totalSubmissions: 0,
      successfulPlacements: 0,
      notes: '',
      status: 'ACTIVE'
    });
    setErrors({});
    setEditMode(false);
    setEditingId(null);
  };

  // Handle edit
  const handleEdit = (vendor) => {
    setFormData({
      companyName: vendor.companyName || '',
      primaryContactName: vendor.primaryContactName || '',
      primaryContactEmail: vendor.primaryContactEmail || '',
      primaryContactPhone: vendor.primaryContactPhone || '',
      address: vendor.address || '',
      city: vendor.city || '',
      state: vendor.state || '',
      zipCode: vendor.zipCode || '',
      country: vendor.country || '',
      preferredSkills: vendor.preferredSkills || '',
      rateRangeMin: vendor.rateRangeMin ? vendor.rateRangeMin.toString() : '',
      rateRangeMax: vendor.rateRangeMax ? vendor.rateRangeMax.toString() : '',
      totalSubmissions: vendor.totalSubmissions || 0,
      successfulPlacements: vendor.successfulPlacements || 0,
      notes: vendor.notes || '',
      status: vendor.status || 'ACTIVE'
    });
    setEditMode(true);
    setEditingId(vendor.id);
    setErrors({});
  };

  // Handle delete confirmation
  const handleDeleteClick = (id) => {
    setDeleteId(id);
    setOpenDialog(true);
  };

  // Handle delete
  const handleDelete = async () => {
    try {
      await axios.delete(`/api/vendors/${deleteId}`);
      toast.success('Vendor deleted successfully!');
      fetchVendors();
      setOpenDialog(false);
      setDeleteId(null);
    } catch (error) {
      console.error('Error deleting vendor:', error);
      toast.error('Failed to delete vendor');
    }
  };

  // Get status color
  const getStatusColor = (status) => {
    switch (status) {
      case 'ACTIVE': return 'success';
      case 'INACTIVE': return 'default';
      case 'PREFERRED': return 'primary';
      case 'SUSPENDED': return 'error';
      default: return 'default';
    }
  };

  // Calculate success rate
  const getSuccessRate = (vendor) => {
    if (!vendor.totalSubmissions || vendor.totalSubmissions === 0) return '0%';
    const rate = (vendor.successfulPlacements / vendor.totalSubmissions) * 100;
    return `${rate.toFixed(1)}%`;
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        Vendor Management
      </Typography>

      {/* Form Section */}
      <Paper elevation={3} sx={{ p: 3, mb: 4 }}>
        <Typography variant="h6" gutterBottom>
          {editMode ? 'Edit Vendor' : 'Add New Vendor'}
        </Typography>

        <Box component="form" onSubmit={handleSubmit}>
          <Grid container spacing={3}>
            {/* Basic Information */}
            <Grid item xs={12}>
              <Typography variant="subtitle1" sx={{ mb: 2, fontWeight: 'bold' }}>
                Company Information
              </Typography>
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                name="companyName"
                label="Company Name"
                value={formData.companyName}
                onChange={handleChange}
                error={!!errors.companyName}
                helperText={errors.companyName}
                required
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <FormControl fullWidth>
                <InputLabel>Status</InputLabel>
                <Select
                  name="status"
                  value={formData.status}
                  onChange={handleChange}
                  label="Status"
                >
                  {STATUS_OPTIONS.map((status) => (
                    <MenuItem key={status} value={status}>
                      {status}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>

            {/* Contact Information */}
            <Grid item xs={12}>
              <Typography variant="subtitle1" sx={{ mb: 2, mt: 2, fontWeight: 'bold' }}>
                Contact Information
              </Typography>
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                name="primaryContactName"
                label="Primary Contact Name"
                value={formData.primaryContactName}
                onChange={handleChange}
                error={!!errors.primaryContactName}
                helperText={errors.primaryContactName}
                required
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                name="primaryContactEmail"
                label="Contact Email"
                type="email"
                value={formData.primaryContactEmail}
                onChange={handleChange}
                error={!!errors.primaryContactEmail}
                helperText={errors.primaryContactEmail}
                required
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                name="primaryContactPhone"
                label="Contact Phone"
                value={formData.primaryContactPhone}
                onChange={handleChange}
              />
            </Grid>

            {/* Address Information */}
            <Grid item xs={12}>
              <Typography variant="subtitle1" sx={{ mb: 2, mt: 2, fontWeight: 'bold' }}>
                Address Information
              </Typography>
            </Grid>

            <Grid item xs={12}>
              <TextField
                fullWidth
                name="address"
                label="Address"
                value={formData.address}
                onChange={handleChange}
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                name="city"
                label="City"
                value={formData.city}
                onChange={handleChange}
              />
            </Grid>

            <Grid item xs={12} sm={3}>
              <TextField
                fullWidth
                name="state"
                label="State"
                value={formData.state}
                onChange={handleChange}
              />
            </Grid>

            <Grid item xs={12} sm={3}>
              <TextField
                fullWidth
                name="zipCode"
                label="Zip Code"
                value={formData.zipCode}
                onChange={handleChange}
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                name="country"
                label="Country"
                value={formData.country}
                onChange={handleChange}
              />
            </Grid>

            {/* Business Information */}
            <Grid item xs={12}>
              <Typography variant="subtitle1" sx={{ mb: 2, mt: 2, fontWeight: 'bold' }}>
                Business Information
              </Typography>
            </Grid>

            <Grid item xs={12}>
              <TextField
                fullWidth
                name="preferredSkills"
                label="Preferred Skills"
                multiline
                rows={3}
                value={formData.preferredSkills}
                onChange={handleChange}
                helperText="Enter skills separated by commas"
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                name="rateRangeMin"
                label="Minimum Rate ($)"
                type="number"
                value={formData.rateRangeMin}
                onChange={handleChange}
                error={!!errors.rateRangeMin}
                helperText={errors.rateRangeMin}
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                name="rateRangeMax"
                label="Maximum Rate ($)"
                type="number"
                value={formData.rateRangeMax}
                onChange={handleChange}
                error={!!errors.rateRangeMax}
                helperText={errors.rateRangeMax}
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                name="totalSubmissions"
                label="Total Submissions"
                type="number"
                value={formData.totalSubmissions}
                onChange={handleChange}
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                name="successfulPlacements"
                label="Successful Placements"
                type="number"
                value={formData.successfulPlacements}
                onChange={handleChange}
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                fullWidth
                name="notes"
                label="Notes"
                multiline
                rows={3}
                value={formData.notes}
                onChange={handleChange}
              />
            </Grid>

            {/* Form Actions */}
            <Grid item xs={12}>
              <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
                <Button
                  type="submit"
                  variant="contained"
                  startIcon={submitLoading ? <CircularProgress size={20} /> : <SaveIcon />}
                  disabled={submitLoading}
                >
                  {editMode ? 'Update Vendor' : 'Create Vendor'}
                </Button>
                <Button
                  variant="outlined"
                  startIcon={<CancelIcon />}
                  onClick={resetForm}
                >
                  Cancel
                </Button>
              </Box>
            </Grid>
          </Grid>
        </Box>
      </Paper>

      {/* Vendors List */}
      <Paper elevation={3} sx={{ p: 3 }}>
        <Typography variant="h6" gutterBottom>
          Vendors List
        </Typography>

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
            <CircularProgress />
          </Box>
        ) : (
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Company Name</TableCell>
                  <TableCell>Primary Contact</TableCell>
                  <TableCell>Email</TableCell>
                  <TableCell>Phone</TableCell>
                  <TableCell>Rate Range</TableCell>
                  <TableCell>Success Rate</TableCell>
                  <TableCell>Status</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {vendors.map((vendor) => (
                  <TableRow key={vendor.id}>
                    <TableCell>{vendor.companyName}</TableCell>
                    <TableCell>{vendor.primaryContactName}</TableCell>
                    <TableCell>{vendor.primaryContactEmail}</TableCell>
                    <TableCell>{vendor.primaryContactPhone}</TableCell>
                    <TableCell>
                      {vendor.rateRangeMin && vendor.rateRangeMax
                        ? `$${vendor.rateRangeMin} - $${vendor.rateRangeMax}`
                        : vendor.rateRangeMin
                        ? `$${vendor.rateRangeMin}+`
                        : vendor.rateRangeMax
                        ? `Up to $${vendor.rateRangeMax}`
                        : 'Not specified'}
                    </TableCell>
                    <TableCell>{getSuccessRate(vendor)}</TableCell>
                    <TableCell>
                      <Chip
                        label={vendor.status}
                        color={getStatusColor(vendor.status)}
                        size="small"
                      />
                    </TableCell>
                    <TableCell>
                      <IconButton
                        size="small"
                        onClick={() => handleEdit(vendor)}
                        color="primary"
                      >
                        <EditIcon />
                      </IconButton>
                      <IconButton
                        size="small"
                        onClick={() => handleDeleteClick(vendor.id)}
                        color="error"
                      >
                        <DeleteIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
                {vendors.length === 0 && (
                  <TableRow>
                    <TableCell colSpan={8} sx={{ textAlign: 'center', py: 4 }}>
                      No vendors found
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        )}
      </Paper>

      {/* Delete Confirmation Dialog */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>Confirm Delete</DialogTitle>
        <DialogContent>
          Are you sure you want to delete this vendor? This action cannot be undone.
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
          <Button onClick={handleDelete} color="error" variant="contained">
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default VendorForm;
