// components/Dashboard/Dashboard.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { dashboardAPI } from '../../services/api';
import toast from 'react-hot-toast';

const Dashboard = () => {
  const [stats, setStats] = useState({
    benchProfiles: 0,
    workingCandidates: 0,
    inInterview: 0,
    placed: 0,
    totalCandidates: 0,
    recentCandidates: [],
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardStats();
  }, []);

  const fetchDashboardStats = async () => {
    try {
      const response = await dashboardAPI.getStats();
      setStats(response.data);
    } catch (error) {
      toast.error('Failed to load dashboard stats');
    } finally {
      setLoading(false);
    }
  };

  const statCards = [
    {
      title: 'Bench Profiles',
      value: stats.benchProfiles,
      color: '#3b82f6',
      bgColor: '#dbeafe',
      link: '/bench-profiles'
    },
    {
      title: 'Working Candidates',
      value: stats.workingCandidates,
      color: '#10b981',
      bgColor: '#d1fae5',
      link: '/working-candidates'
    },
    {
      title: 'In Interview',
      value: stats.inInterview,
      color: '#f59e0b',
      bgColor: '#fef3c7',
      link: '/candidates?status=INTERVIEW'
    },
    {
      title: 'Placed',
      value: stats.placed,
      color: '#8b5cf6',
      bgColor: '#e0e7ff',
      link: '/candidates?status=PLACED'
    },
    {
      title: 'Total Candidates',
      value: stats.totalCandidates,
      color: '#6b7280',
      bgColor: '#f3f4f6',
      link: '/candidates'
    },
  ];

  const getStatusBadgeClass = (status) => {
    const statusMap = {
      'BENCH': 'status-bench',
      'WORKING': 'status-working',
      'INTERVIEW': 'status-interview',
      'PLACED': 'status-placed',
    };
    return `status-badge ${statusMap[status] || 'status-bench'}`;
  };

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '400px' }}>
        <div>Loading dashboard...</div>
      </div>
    );
  }

  return (
    <div>
      <div style={{ marginBottom: '2rem' }}>
        <h1 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '0.5rem' }}>
          Dashboard
        </h1>
        <p style={{ color: '#6b7280' }}>
          Overview of your consultancy's candidate management
        </p>
      </div>

      {/* Stats Grid */}
      <div className="dashboard-grid">
        {statCards.map((card, index) => (
          <Link 
            key={index} 
            to={card.link} 
            style={{ textDecoration: 'none', color: 'inherit' }}
          >
            <div 
              className="stat-card"
              style={{ 
                borderLeft: `4px solid ${card.color}`,
                backgroundColor: card.bgColor,
                border: `1px solid ${card.color}20`
              }}
            >
              <div className="stat-number" style={{ color: card.color }}>
                {card.value}
              </div>
              <div className="stat-label">
                {card.title}
              </div>
            </div>
          </Link>
        ))}
      </div>

      {/* Recent Candidates */}
      <div className="recent-candidates">
        <div className="section-header">
          <h2 className="section-title">Recent Candidates</h2>
        </div>
        
        {stats.recentCandidates.length > 0 ? (
          <div className="table-container">
            <table className="table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Primary Skill</th>
                  <th>Experience</th>
                  <th>Location</th>
                  <th>Visa Status</th>
                  <th>Status</th>
                  <th>Added</th>
                </tr>
              </thead>
              <tbody>
                {stats.recentCandidates.map((candidate) => (
                  <tr key={candidate.id}>
                    <td style={{ fontWeight: '600' }}>{candidate.fullName}</td>
                    <td>{candidate.primarySkill}</td>
                    <td>{candidate.experienceYears} years</td>
                    <td>{candidate.location}</td>
                    <td>
                      <span style={{ 
                        padding: '0.25rem 0.5rem', 
                        backgroundColor: '#f3f4f6', 
                        borderRadius: '4px',
                        fontSize: '0.875rem'
                      }}>
                        {candidate.visaStatus}
                      </span>
                    </td>
                    <td>
                      <span className={getStatusBadgeClass(candidate.status)}>
                        {candidate.status}
                      </span>
                    </td>
                    <td style={{ color: '#6b7280', fontSize: '0.875rem' }}>
                      {new Date(candidate.createdAt).toLocaleDateString()}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div style={{ padding: '3rem', textAlign: 'center', color: '#6b7280' }}>
            <p>No candidates found</p>
            <Link 
              to="/candidates/new" 
              style={{ 
                color: '#3b82f6', 
                textDecoration: 'none',
                fontWeight: '500'
              }}
            >
              Add your first candidate
            </Link>
          </div>
        )}
      </div>

      {/* Quick Actions */}
      <div style={{ marginTop: '2rem' }}>
        <div style={{ 
          background: 'white', 
          borderRadius: '12px', 
          boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
          padding: '2rem'
        }}>
          <h3 style={{ marginBottom: '1rem', fontSize: '1.25rem', fontWeight: '600' }}>
            Quick Actions
          </h3>
          <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
            <Link to="/candidates/new" className="btn-primary" style={{ textDecoration: 'none', width: 'auto' }}>
              ➕ Add New Candidate
            </Link>
            <Link to="/bench-profiles" className="btn-secondary" style={{ textDecoration: 'none' }}>
              👥 View Bench Profiles
            </Link>
            <Link to="/candidates" className="btn-secondary" style={{ textDecoration: 'none' }}>
              📋 All Candidates
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
