import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { benchCandidatesAPI, employeesAPI } from '../../services/api';
import toast from 'react-hot-toast';

const BenchCandidateForm = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const isEdit = Boolean(id);

  const [formData, setFormData] = useState({
    fullName: '',
    visaStatus: 'H1B',
    city: '',
    state: '',
    primarySkill: '',
    experienceYears: '',
    phoneNumber: '',
    email: '',
    targetRate: '',
    assignedConsultantId: '',
    notes: '',
  });

  const [employees, setEmployees] = useState([]);
  const [documents, setDocuments] = useState([]);
  const [currentResumeFilename, setCurrentResumeFilename] = useState('');
  const [loading, setLoading] = useState(false);
  const [loadingCandidate, setLoadingCandidate] = useState(isEdit);

  useEffect(() => {
    fetchEmployees();
    if (isEdit) {
      fetchCandidate();
    }
  }, [id, isEdit]);

  const fetchEmployees = async () => {
    try {
      const response = await employeesAPI.getAll();
      setEmployees(response.data);
    } catch (error) {
      toast.error('Failed to load employees');
    }
  };

  const fetchCandidate = async () => {
    try {
      const response = await benchCandidatesAPI.getById(id);
      const candidate = response.data;
      setFormData({
        fullName: candidate.fullName || '',
        visaStatus: candidate.visaStatus || 'H1B',
        city: candidate.city || '',
        state: candidate.state || '',
        primarySkill: candidate.primarySkill || '',
        experienceYears: candidate.experienceYears || '',
        phoneNumber: candidate.phoneNumber || '',
        email: candidate.email || '',
        targetRate: candidate.targetRate || '',
        assignedConsultantId: candidate.assignedConsultantId || '',
        notes: candidate.notes || '',
      });
      setCurrentResumeFilename(candidate.resumeFilename || '');
    } catch (error) {
      toast.error('Failed to load candidate details');
      navigate('/bench-candidates');
    } finally {
      setLoadingCandidate(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    if (files.length === 0) return;

    // Validate files
    const allowedTypes = [
      'application/pdf',
      'application/msword',
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
    ];

    const invalidFiles = files.filter(file => !allowedTypes.includes(file.type));
    if (invalidFiles.length > 0) {
      toast.error('Please select only PDF or Word documents');
      e.target.value = '';
      return;
    }

    // Validate file sizes (max 10MB each)
    const oversizedFiles = files.filter(file => file.size > 10 * 1024 * 1024);
    if (oversizedFiles.length > 0) {
      toast.error('Each file must be less than 10MB');
      e.target.value = '';
      return;
    }

    setDocuments(files);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const submitData = new FormData();

      // Append form fields
      Object.keys(formData).forEach(key => {
        if (formData[key] !== '') {
          submitData.append(key, formData[key]);
        }
      });

      // Append document files if selected
      if (documents.length > 0) {
        documents.forEach(doc => {
          submitData.append('documents', doc);
        });
      }

      let response;
      if (isEdit) {
        response = await benchCandidatesAPI.update(id, submitData);
        toast.success('Bench candidate updated successfully!');
      } else {
        response = await benchCandidatesAPI.create(submitData);
        toast.success('Bench candidate created successfully!');
      }

      navigate('/bench-candidates');
    } catch (error) {
      const message = error.response?.data?.message || 
        `Failed to ${isEdit ? 'update' : 'create'} bench candidate`;
      toast.error(message);
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    navigate('/bench-candidates');
  };

  if (loadingCandidate) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '400px' }}>
        <div>Loading candidate details...</div>
      </div>
    );
  }

  return (
    <div>
      <div style={{ marginBottom: '1.5rem' }}>
        <h1 style={{ fontSize: '1.75rem', fontWeight: 'bold', color: '#1F2937', marginBottom: '0.5rem' }}>
          {isEdit ? 'Edit Bench Candidate' : 'Add New Bench Candidate'}
        </h1>
        <p style={{ color: '#6B7280' }}>
          {isEdit ? 'Update bench candidate information' : 'Fill in the candidate details for bench profile'}
        </p>
      </div>

      <div className="form-container" style={{ background: 'white', borderRadius: '12px', boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)', padding: '1.5rem' }}>
        <form onSubmit={handleSubmit}>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '0.75rem' }}>
            {/* Full Name */}
            <div className="form-group" style={{ margin: '0 0 0.75rem 0' }}>
              <label htmlFor="fullName" className="form-label" style={{ marginBottom: '0.25rem', display: 'block', fontSize: '0.875rem' }}>
                Candidate Name *
              </label>
              <input
                type="text"
                id="fullName"
                name="fullName"
                className="form-input"
                value={formData.fullName}
                onChange={handleChange}
                required
                placeholder="Enter candidate full name"
                style={{ padding: '0.5rem', width: '100%' }}
              />
            </div>

            {/* Visa Status */}
            <div className="form-group" style={{ margin: '0 0 0.75rem 0' }}>
              <label htmlFor="visaStatus" className="form-label" style={{ marginBottom: '0.25rem', display: 'block', fontSize: '0.875rem' }}>
                Visa Status *
              </label>
              <select
                id="visaStatus"
                name="visaStatus"
                className="form-input"
                value={formData.visaStatus}
                onChange={handleChange}
                required
                style={{ padding: '0.5rem', width: '100%' }}
              >
                <option value="H1B">H1B</option>
                <option value="OPT">OPT</option>
                <option value="GC">Green Card</option>
                <option value="CITIZEN">US Citizen</option>
                <option value="F1">F1</option>
                <option value="L1">L1</option>
                <option value="OTHER">Other</option>
              </select>
            </div>

            {/* City */}
            <div className="form-group" style={{ margin: '0 0 0.75rem 0' }}>
              <label htmlFor="city" className="form-label" style={{ marginBottom: '0.25rem', display: 'block', fontSize: '0.875rem' }}>
                City *
              </label>
              <input
                type="text"
                id="city"
                name="city"
                className="form-input"
                value={formData.city}
                onChange={handleChange}
                required
                placeholder="e.g. New York"
                style={{ padding: '0.5rem', width: '100%' }}
              />
            </div>

            {/* State */}
            <div className="form-group" style={{ margin: '0 0 0.75rem 0' }}>
              <label htmlFor="state" className="form-label" style={{ marginBottom: '0.25rem', display: 'block', fontSize: '0.875rem' }}>
                State *
              </label>
              <input
                type="text"
                id="state"
                name="state"
                className="form-input"
                value={formData.state}
                onChange={handleChange}
                required
                placeholder="e.g. NY"
                style={{ padding: '0.5rem', width: '100%' }}
              />
            </div>

            {/* Primary Skill */}
            <div className="form-group" style={{ margin: '0 0 0.75rem 0' }}>
              <label htmlFor="primarySkill" className="form-label" style={{ marginBottom: '0.25rem', display: 'block', fontSize: '0.875rem' }}>
                Primary Skill *
              </label>
              <input
                type="text"
                id="primarySkill"
                name="primarySkill"
                className="form-input"
                value={formData.primarySkill}
                onChange={handleChange}
                required
                placeholder="e.g. Java Developer, React"
                style={{ padding: '0.5rem', width: '100%' }}
              />
            </div>

            {/* Experience Years */}
            <div className="form-group" style={{ margin: '0 0 0.75rem 0' }}>
              <label htmlFor="experienceYears" className="form-label" style={{ marginBottom: '0.25rem', display: 'block', fontSize: '0.875rem' }}>
                Experience (Years) *
              </label>
              <input
                type="number"
                id="experienceYears"
                name="experienceYears"
                className="form-input"
                value={formData.experienceYears}
                onChange={handleChange}
                required
                min="0"
                max="50"
                placeholder="e.g. 5"
                style={{ padding: '0.5rem', width: '100%' }}
              />
            </div>

            {/* Phone Number */}
            <div className="form-group" style={{ margin: '0 0 0.75rem 0' }}>
              <label htmlFor="phoneNumber" className="form-label" style={{ marginBottom: '0.25rem', display: 'block', fontSize: '0.875rem' }}>
                Phone Number *
              </label>
              <input
                type="tel"
                id="phoneNumber"
                name="phoneNumber"
                className="form-input"
                value={formData.phoneNumber}
                onChange={handleChange}
                required
                placeholder="(555) 123-4567"
                style={{ padding: '0.5rem', width: '100%' }}
              />
            </div>

            {/* Email */}
            <div className="form-group" style={{ margin: '0 0 0.75rem 0' }}>
              <label htmlFor="email" className="form-label" style={{ marginBottom: '0.25rem', display: 'block', fontSize: '0.875rem' }}>
                Email *
              </label>
              <input
                type="email"
                id="email"
                name="email"
                className="form-input"
                value={formData.email}
                onChange={handleChange}
                required
                placeholder="candidate@email.com"
                style={{ padding: '0.5rem', width: '100%' }}
              />
            </div>

            {/* Target Rate */}
            <div className="form-group" style={{ margin: '0 0 0.75rem 0' }}>
              <label htmlFor="targetRate" className="form-label" style={{ marginBottom: '0.25rem', display: 'block', fontSize: '0.875rem' }}>
                Looking Rate ($/hr)
              </label>
              <input
                type="number"
                id="targetRate"
                name="targetRate"
                className="form-input"
                value={formData.targetRate}
                onChange={handleChange}
                min="0"
                step="0.01"
                placeholder="e.g. 75"
                style={{ padding: '0.5rem', width: '100%' }}
              />
            </div>

            {/* Assigned Consultant */}
            <div className="form-group" style={{ margin: '0 0 0.75rem 0', gridColumn: 'span 3' }}>
              <label htmlFor="assignedConsultantId" className="form-label" style={{ marginBottom: '0.25rem', display: 'block', fontSize: '0.875rem' }}>
                Assigned Consultant (Optional)
              </label>
              <select
                id="assignedConsultantId"
                name="assignedConsultantId"
                className="form-input"
                value={formData.assignedConsultantId}
                onChange={handleChange}
                style={{ padding: '0.5rem', width: '100%' }}
              >
                <option value="">Select Consultant (Optional)</option>
                {employees.map(employee => (
                  <option key={employee.id} value={employee.id}>
                    {employee.fullName} - {employee.role}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* Notes */}
          <div className="form-group" style={{ margin: '0.75rem 0' }}>
            <label htmlFor="notes" className="form-label" style={{ marginBottom: '0.25rem', display: 'block', fontSize: '0.875rem' }}>
              Notes
            </label>
            <textarea
              id="notes"
              name="notes"
              className="form-input"
              value={formData.notes}
              onChange={handleChange}
              rows="3"
              placeholder="Additional notes about the candidate..."
              style={{ resize: 'vertical', padding: '0.5rem', width: '100%' }}
            />
          </div>

          {/* Document Upload */}
          <div className="form-group" style={{ margin: '0.75rem 0' }}>
            <label htmlFor="documents" className="form-label" style={{ marginBottom: '0.25rem', display: 'block', fontSize: '0.875rem' }}>
              Upload Documents *
            </label>
            {currentResumeFilename && (
              <div style={{ 
                marginBottom: '0.5rem', 
                padding: '0.5rem', 
                backgroundColor: '#f3f4f6', 
                borderRadius: '4px',
                fontSize: '0.875rem'
              }}>
                Current file: <strong>{currentResumeFilename}</strong>
              </div>
            )}
            <input
              type="file"
              id="documents"
              name="documents"
              className="form-input"
              onChange={handleFileChange}
              accept=".pdf,.doc,.docx"
              required={!isEdit && !currentResumeFilename}
              multiple
              style={{ padding: '0.5rem', width: '100%' }}
            />
            <div style={{ fontSize: '0.75rem', color: '#6b7280', marginTop: '0.25rem' }}>
              Accepted formats: PDF, DOC, DOCX (Max size: 10MB per file)
            </div>
          </div>

          {/* Form Actions */}
          <div style={{ display: 'flex', gap: '1rem', marginTop: '1.5rem', justifyContent: 'flex-end' }}>
            <button
              type="button"
              onClick={handleCancel}
              className="btn-secondary"
              disabled={loading}
              style={{ padding: '0.5rem 1rem' }}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn-primary"
              disabled={loading}
              style={{ padding: '0.5rem 1rem' }}
            >
              {loading 
                ? (isEdit ? 'Updating...' : 'Creating...') 
                : (isEdit ? 'Update Candidate' : 'Create Candidate')
              }
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BenchCandidateForm;
